import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import { fetchData } from './Home-Api';

const initialState = {
    articles: [],
    status: 'idle',
    error: null
}

export const fetchDataAsync = createAsyncThunk(
    'home/fetchData', 
    async (searchText) => {
        const response = await fetchData(searchText || 'apple');
        return response.data;
    }
);

export const homeSlice = createSlice({
    name: 'home',
    initialState,
    reducers: {
        clearState: () => {
            return initialState;
        }
    },
    extraReducers: (builder) => {
        builder
        .addCase(fetchDataAsync.pending, (state) => {
            state.status = 'loading';
        })
        .addCase(fetchDataAsync.fulfilled, (state, action) => {
            state.status = 'idle';
            state.articles = action.payload.articles;
        })
        .addCase(fetchDataAsync.rejected, (state) => {
            state.status = 'idle';
            state.error = 'error';
        })
    },
});

export const {clearState} = homeSlice.actions;
export const selectData = (state) => state.home.articles;
export const status = (state) => state.home.status;
export const error = (state) => state.home.error;
export default homeSlice.reducer;
