import React, { useState, useEffect } from "react";
import Loader from './Loader';
import { useSelector, useDispatch } from 'react-redux';
import { fetchDataAsync, selectData, status, clearState } from './homeSlice';
import { Form, Col, Button, Row, FormControl, InputGroup, Card, Container, Drop } from 'react-bootstrap';
import Cards from "./cards";

function Home() {
    const sources = [
        'tesla',
        'apple',
        'business',
        'techcrunch',
        'wallStreet'
    ];
    const articles = useSelector(selectData);
    const loadingStatus = useSelector(status);
    const dispatch = useDispatch();
    const [searchText, setSearchText] = useState('tesla');

    const handleClick = event => {
        setSearchText(event.target.name);
        dispatch(clearState());
    }
    useEffect(() => {
        articles.length === 0 && dispatch(fetchDataAsync(searchText));
    }, [articles.length, searchText, dispatch])

    return (

             <div className='home-page'>
                  <Container>
            <Form>
                <Row className="align-items-center">
                    <Col xs="auto">
                        <Form.Label htmlFor="inlineFormInput" visuallyHidden>
                        </Form.Label>
                        <Form.Control
                        className="mb-2"
                        id="inlineFormInput"
                        placeholder="Type text to search"
                        name='searchText'
                        onChange={event => setSearchText(event.target.value)}/>
                    </Col>
                    <Col xs="auto">
                        <Button type="submit" className="mb-2" onClick={() => 
                        dispatch(clearState())}>
                            Search
                        </Button>
                    </Col>
                </Row>
            </Form>
            <Row>
                <div className='sourceContainer'>
                <DropdownButton id="dropdown-basic-button" title="Dropdown button">
                 {sources.map((value, index) => 
                 <Dropdown.Item key={index} name={value} onClick={handleClick}>{value}</Dropdown.Item>
                )}
                </DropdownButton>

                </div>
                {loadingStatus === 'loading' ? <Loader /> : articles.length > 0 ? articles.map((value, index) => 
                <Cards key={index} data={value} />) : <p>There are no articles matching your request</p>
                }
            </Row>
        </Container>
        </div>
    )
}


export default Home;
