import './App.css';
import { useState } from 'react';

// {/* <li><label><input type='checkbox'></input>Go to job</label></li>,
//   <li><label><input type='checkbox'></input>Call to Mom</label></li>,
//   <li><label><input type='checkbox'></input>Do shopping</label></li> */}
function App() {
  const heading = 'TODO LIST';
  const [todo, setTodo] = useState('');
  const todos = ['Go to job', 'Call to Mom', 'Do shopping'];
  const [allTodos, addTodo] = useState(todos.map((el, i) => <li key={i}>
    <label><input type='checkbox'></input></label>{el}
  </li>))
 const handleSubtmit = e => {
   e.preventDefault();
   if (!todo) return;
   addTodo(todo);
   setTodo("");
 }
 const addTodos = text => {
   const newTodos = [...todos, {text}];
   addTodo(newTodos)
 }

  return (
    <div className="App">
      <div className='box'>
         <h1>{heading}</h1>
         <input type='text' value={todo} onChange={e => setTodo(e.target.value)}></input>
          <button type='button' onClick={handleSubtmit}>add</button>
         <div className='list'>
          <ul>
           {allTodos}
          </ul>
         </div>
      </div>

    </div>
  );
}

export default App;
