import React from "react";
import { useParams } from 'react-router-dom';
import { useSelector } from 'react-redux';
import Cards from "./cards";
import { useDispatch } from "react-redux";
import Loader from "./Loader";
import { useEffect, useState } from "react";
import { fetchDataAsync, selectData, status, clearState } from './homeSlice';
import {Row, Col, Container} from 'react-bootstrap';

function BBCNews() {
    const articles = useSelector(selectData);
    const loadingStatus = useSelector(status);
    const dispatch = useDispatch();
    const [searchText, setSearchText] = useState('BBC');
    const handleClick = event => {
        setSearchText(event.target.name);
        dispatch(clearState());
    }
    useEffect(() => {
        articles.length === 0 && dispatch(fetchDataAsync(searchText));
    }, [articles.length, searchText, dispatch])
    return (
        <div className='bbc-page'>
            <Container>
           <Row className='justify-content-md-center'>
                <Col md='auto'>
                {loadingStatus === 'loading' ? <Loader /> : articles.length > 0 ? articles.map((value, index) => 
                <Cards key={index} data={value} />) : <p className='noArticle'>There are no articles matching your request</p>
                }
                </Col>
            </Row>
            </Container>
        </div>
    )
}

export default BBCNews;
