import React, { useState } from "react";
import { Card, Button, Row, Col, Accordion } from 'react-bootstrap';
import PropTypes from 'prop-types';
import Placeholder from '../style/unnamed.png';

const Cards = ({ data }) => {
    const [state, setState] = useState(false)

    const handleClick = () => {
        setState(true);
    }

    return (
        <div className='news-cards'>
                <Row>
                    <Col></Col>
                    <Col sm={8}>
                    <Card>
                    <Card.Header className='movie-title'>Movie Title: {data.title}</Card.Header>
                    <Card.Img variant="top" src={typeof data.poster === 'string' ? data.poster : Placeholder} alt={data.title}/>
                    <Card.Body>
                    <Card.Title style={{fontWeight:"bold", fontSize: '1rem'}}>{data.plot}</Card.Title>
                    <Card.Link href={data.trailer.link}>Trailer</Card.Link>
                    <Card.Text className='movie-info'>
                    <b>Year:</b> {data.year}. <br /> <b>Movie length:</b>{data.length}. <br /><b>Movie rating:</b> {data.rating}</Card.Text>
                    <Button variant="primary" onClick={handleClick}>Read more</Button>
                    <Accordion defaultActiveKey="0">
  <Accordion.Item eventKey="0">
    <Accordion.Header>Read more</Accordion.Header>
    <Accordion.Body>
      Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod
      tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim
      veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea
      commodo consequat. Duis aute irure dolor in reprehenderit in voluptate
      velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat
      cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id
      est laborum.
    </Accordion.Body>
  </Accordion.Item>
</Accordion>
                    </Card.Body>
                    </Card>
                    </Col>
                    <Col></Col>
                </Row>
        </div>
    )
}

Cards.propTypes = {
    data: PropTypes.object
}

export default Cards;
