import React, { useState,useEffect } from "react";
import PropTypes from 'prop-types';
import Loader from './Loader';
import { useSelector, useDispatch } from 'react-redux';
import { fetchDataAsync, selectData, status, clearState } from './homeSlice';
import { Form, Col, Button, Row, FormControl, InputGroup, Card } from 'react-bootstrap';
import Cards from "./cards";

function Home(props) {
    const sources = [
        'tesla',
        'apple',
        'business',
        'techCrunch',
        'wallStreet'
    ];
    const articles = useSelector(selectData);
    const loadingStatus = useSelector(status);
    const dispatch = useDispatch();
    const [searchText, setSearchText] = useState('tesla');

    const handleClick = event => {
        setSearchText(event.target.name);
        dispatch(clearState());
    }
    useEffect(() => {
        articles.length === 0 && dispatch(fetchDataAsync(searchText));
    }, [articles.length, searchText, dispatch])

    return (
        <div className='home-page'>
            <Form>
                <Row className="align-items-center">
                    <Col xs="auto">
                        <Form.Label htmlFor="inlineFormInput" visuallyHidden>
                        </Form.Label>
                        <Form.Control
                        className="mb-2"
                        id="inlineFormInput"
                        placeholder="Type text to search"
                        name='searchText'
                        onChange={event => setSearchText(event.target.value)}/>
                    </Col>
                    <Col xs="auto">
                        <Button type="submit" className="mb-2" onClick={() => 
                        dispatch(clearState())}>
                            Search
                        </Button>
                    </Col>
                </Row>
            </Form>
            <div className='sourceContainer'>
                {sources.map((value, index) => {
                    <button key={index} onClick={handleClick} name={value}>{value}</button>
                })}
            </div>
            {loadingStatus === 'loading' ? <Loader /> : articles.length > 0 ? articles.map((value, index) => 
                <Cards key={index} data={value} />) : <p></p>
            }
        </div>
    )
}

Home.propTypes = {

}

export default Home;
