import React from "react";
import { Card, Button, Container, Row, Col } from 'react-bootstrap';
import PropTypes from 'prop-types';
import unnamed from '/style'

const Cards = ({ data }) => {
    console.log(data.urlToImage)
    return (
        <div className='news-cards'>
            <Container>
                <Row>
                    <Col>
                    <Card>
                    <Card.Header>Source: {data.source.name}</Card.Header>
                    <Card.Img variant="top" src={data.urlToImage ? data.urlToImage : "./news-app/src/style/unnamed.png"}  alt={data.title}/>
                    <Card.Body>
                    <Card.Title style={{fontWeight:"bold"}}>{data.title}</Card.Title>
                    <Card.Text>{data.description}</Card.Text>
                    <Button variant="primary" onClick={event => window.location.href=`${data.url}`}>Read More</Button>
                    </Card.Body>
                    </Card>
                    </Col>
                </Row>
            </Container>
        </div>
    )
}

Cards.propTypes = {
    data: PropTypes.object
}

export default Cards;
