import './App.css';
import { useState } from 'react';

function App() {
  const [count, setCount] = useState(0);
  const [heading, setColor] = useState('Hello world');
  function changeColor() {
    setColor(headin)
  }
  function increment() {
    if (count < 10) {
      setCount(count + 1)
    }

  }
  function decrement() {
    if (count > 0) {
      setCount(count - 1)
    }

  }
  return (
    <div className="App">
      <h1>{count}</h1>
      <h2>{heading}</h2>
      <button onClick={increment}>Increment</button>
      <button onClick={decrement}>Decrement</button>
      <input type='color' onChange={changeColor} value='#e66465'></input>
    </div>
  );
}

export default App;
