import React from "react";
import { Card, Button, Container, Row, Col } from 'react-bootstrap';
import PropTypes from 'prop-types';
import Placeholder from '../style/unnamed.png';

const Cards = ({ data }) => {
    console.log(data)
    return (
        <div className='news-cards'>

                <Row>
                    <Col>
                    <Card>
                    <Card.Header className='movie-title'>Movie Title: {data.title}</Card.Header>
                    <Card.Img variant="top" src={typeof data.poster === 'string' ? data.poster : Placeholder} alt={data.title}/>
                    <Card.Body>
                    <Card.Title style={{fontWeight:"bold"}}>{data.plot}</Card.Title>
                    <Card.Link href={data.trailer.link}>Trailer</Card.Link>
                    <Card.Text className='movie-info'>
                    <b>Year: </b> {data.year}. <br /> <b> Movie length: </b> {data.length}. <br /> Movie rating: {data.rating}</Card.Text>
                    </Card.Body>
                    </Card>
                    </Col>
                </Row>

        </div>
    )
}

Cards.propTypes = {
    data: PropTypes.object
}

export default Cards;
