import React from 'react';
import './App.css';
import Page404 from './components/404';
import BBCNews from './components/BBC News';
import Home from './components/Home';
import { BrowserRouter as Router, Link, Route, Routes } from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  return(  
  <Router>
    <div className='news-app'>
      <Container>
        <header>
        <h1 className='app-heading'>News App</h1>
      </header>
      <Nav className='me-auto'>
        <ul>
          <li>
            <Link to='/'>Home</Link>
          </li>
          <li>
            <Link to='/BBCNews'>BBC News</Link>
          </li>
          <li>
            <Link to='/Page404'>Page404</Link>
          </li>
        </ul>
      </Nav>
      <Routes>
        <Route exact path='/' element={<Home />} />
        <Route exact path='/BBCNews' element={<BBCNews />} />
        <Route exact path='/Page404' element={<Page404 />} />
      </Routes>
      </Container>
    </div>
  </Router>
  );
}

export default App;
