import './App.css';
import { useState } from 'react';

function App() {
  const heading = 'TODO LIST';
  const [todo, setTodo] = useState('');
  const todos = [<li><label><input type='checkbox'></input>Go to job</label></li>,
  <li><label><input type='checkbox'></input>Call to Mom</label></li>,
  <li><label><input type='checkbox'></input>Do shopping</label></li>]
  const [allTodos, addTodo] = useState(toods)
  function addTodos() {
    addTodo(allTodos.push(<li><label><input type='checkbox'></input>{todo}</label></li>));
    console.log(allTodos)
  }

  return (
    <div className="App">
      <div className='box'>
         <h1>{heading}</h1>
         <input type='text' value={todo} onChange={e => setTodo(e.target.value)}></input>
          <button type='button' onClick={addTodos}>add</button>
         <div className='list'>
          <ul>
           {allTodos}
          </ul>
         </div>
      </div>

    </div>
  );
}

export default App;
